	    var tags = [ "script", "a ", "div", "span", "select", "button", "i" ];
	    
	    for(var i = 0; i < tags.length; i++) {
	    	var tag = tags[i];
html = html.replace(new RegExp('<' + tag + '([^>]*?)/>', 'img'), '<' + tag + '$1></' + tag + '>');
	    }
	    
		return html;
