		if (w != "" && !isNaN(w))
			this.cacheWidth = parseInt(w);
		if (h != "" && !isNaN(h))
			this.cacheHeight = parseInt(h);
		
		if (!this.shim)
			return;
			
		if (x !== "")
			this.shim.style.left = x + 'px';
		if (y !== "")
			this.shim.style.top = y + 'px';
		
